import React, { useRef } from "react";
import { View, Image, Text, ScrollView, ImageBackground } from "react-native";
import MainScreenStyles from "./styles/MainScreenStyles";
import MainScreenHeader from "./components/MainScreenHeader";

const MainScreen = () => {
  const scrollViewRef = useRef(null);
  return (
    <ScrollView
      style={MainScreenStyles.mainContainer}
      contentContainerStyle={{ flex: 1 }}
      ref={scrollViewRef}
    >
      <MainScreenHeader scrollViewRef={scrollViewRef} />
      <ImageBackground
        source={require("../../../assets/background-coffee-laptop.jpg")}
        style={MainScreenStyles.backgroundImage}
        resizeMode={"cover"}
      >
        <View>
          <View style={MainScreenStyles.container}>
            <Text style={MainScreenStyles.titleText}> Welcome to Webify </Text>
            <Text style={MainScreenStyles.descriptionText}>
              Start your internet journey with our morning cup of coffee
            </Text>
            <View style={MainScreenStyles.canvas}></View>
          </View>
          <View style={MainScreenStyles.learnMoreContainer}>
            <Text>Start</Text>
          </View>
        </View>
      </ImageBackground>
    </ScrollView>
  );
};

export default MainScreen;
