import React, { useRef } from "react";
import { View, Image, Text, ScrollView, ImageBackground } from "react-native";
import MainScreenStyles from "./styles/MainScreenStyles";
import MainScreenHeader from "./components/MainScreenHeader";

const MainScreen = () => {
  const scrollViewRef = useRef(null);
  return (
    <ScrollView
      style={MainScreenStyles.mainContainer}
      contentContainerStyle={{ flex: 1 }}
      ref={scrollViewRef}
    >
      <ImageBackground
        source={require("../../../assets/cool-background.png")}
        style={MainScreenStyles.image}
        resizeMode={"cover"}
      >
        <MainScreenHeader />
        <View style={MainScreenStyles.container}>
          <Text style={{ fontSize: 30 }}> Welcome to Webify </Text>
        </View>
      </ImageBackground>
    </ScrollView>
  );
};

export default MainScreen;
