import { StyleSheet } from "react-native";

export default StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "flex-start",
    marginTop: 80,
  },
  title: {
    marginLeft: 100,
    fontSize: 30,
  },
  description: {
    fontSize: 20,
    marginTop: 100,
    width: 400,
    flexDirection: "row",
  },
  endingText: {
    marginTop: 10,
  },
  image: { 
      width: 300, 
      height: 200
  }
});
