import { StyleSheet } from "react-native";

export default StyleSheet.create({
  mainContainer: {
    flex: 1,
  },
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "flex-start",
    marginLeft: 300,
    paddingBottom: 100
  },
  image: {
    flex: 1,
    width: "100%",
    height: "100%",
  },
  backgroundImage: {
    height: 300,
    width: "100%",
    alignSelf: "center",
  },
  titleText: {
    fontSize: 30,
  },
  descriptionText: {
    marginTop: 50,
    fontSize: 20,
  },
});
